import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CounterApp from './CounterApp'; // Import the CounterApp component

const App = () => {
  const [users, setUsers] = useState([]);
  const [showCounter, setShowCounter] = useState(false); // Updated state name

  useEffect(() => {
    axios.get('http://localhost:3000/api/users')
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleRefresh = () => {
    axios.get('http://localhost:3000/api/users')
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  const handleToggleCounter = () => {
    setShowCounter(!showCounter); // Updated function name
  };

  const renderCounterSection = () => {
    if (showCounter) {
      return (
        <div>
          <CounterApp /> {/* Render the CounterApp component */}
        </div>
      );
    }
    return null;
  };

  return (
    <div>
      <h1>User List</h1>
      <button onClick={handleRefresh}>Refresh</button>
      <ul>
        {users.map(user => (
          <li key={user.id}>{user.name}</li>
        ))}
      </ul>

      <button onClick={handleToggleCounter}>Toggle Counter</button>

      {renderCounterSection()}
    </div>
  );
};

export default App;
